from typing import List


class Solution:
    def longestSubarray(self, nums: List[int]) -> int:
        mx = max(nums)
        large = 0 
        curr = 0
        for i in range(len(nums)) :
            if nums[i] == mx :
                curr += 1 
                large = max(large,curr)
            else :
                curr = 0 
        return large 


